IF EXISTS(SELECT 1 FROM sys.tables WHERE object_id = OBJECT_ID('Employe'))
BEGIN;
    DROP TABLE [Employe];
END;
GO

CREATE TABLE [Employe] (
    [EmployeID] INTEGER NOT NULL IDENTITY(1, 1),
    [Nom] VARCHAR(255) NULL,
    [Prenom] VARCHAR(255) NULL,
    [Estlivreur] VARCHAR(255) NULL,
    PRIMARY KEY ([EmployeID])
);
GO

